        [HttpPost]
        public IActionResult Emp_Sign_Form([FromBody]Employee_Signature obj)
        {
            try
            {    if (obj!=null)
                {
                    InsertUpdateResponse Data = employee.InsertUpdate_EmployeeSign(obj);
                    if (Data.ResponseCode==0)
                    {
                        TempData["Output"] = "Record submitted successfully.";
                    }
                    else if (Data.ResponseCode==1)
                    {
                        TempData["Output"] = "Record updated successfully";
                    }
                    else if (Data.ResponseCode==2)
                    {
                        TempData["Output"] = "Record already Exists";
                    }
                    else 
                    {
                        TempData["Output"] = "Something went wrong. Please try again.";
                    }       
                    return Json(Data.SignaturesList);
                }
                    return Json(null);

            }
            catch (Exception)
            {
                throw;
            }

            
           
        }

